import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Box, Snackbar, Alert, Fab, Tooltip, Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store/store';
import { ThemeProvider } from './contexts/ThemeContext';
import { usePWA } from './hooks/usePWA';
import { 
  InstallMobile as InstallIcon,
  CloudOff as OfflineIcon,
  Wifi as OnlineIcon,
  Notifications as NotificationsIcon
} from '@mui/icons-material';

// Páginas
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/DashboardPage';
import EventsPage from './pages/EventsPage';
import CreateEventPage from './pages/CreateEventPage';
import FriendsPage from './pages/FriendsPage';
import ProfilePage from './pages/ProfilePage';
import EventDetailsPage from './pages/EventDetailsPage';
import NotificationsPage from './pages/NotificationsPage';
import MessagesPage from './pages/MessagesPage';
import SettingsPage from './pages/SettingsPage';
import DemoPage from './pages/DemoPage';

// Componentes
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

// Hook para verificar autenticação
import { useAppSelector } from './hooks/redux';

function PWAManager() {
  const {
    isOnline,
    isInstallable,
    isInstalled,
    isLoading,
    notificationPermission,
    installApp,
    requestNotificationPermission,
    showNotification,
    isSupported
  } = usePWA();

  const [installDialogOpen, setInstallDialogOpen] = React.useState(false);
  const [notificationDialogOpen, setNotificationDialogOpen] = React.useState(false);
  const [offlineSnackbarOpen, setOfflineSnackbarOpen] = React.useState(false);

  // Verificar suporte PWA e exibir prompt de instalação após delay
  useEffect(() => {
    if (isInstallable && !isInstalled) {
      const timer = setTimeout(() => {
        setInstallDialogOpen(true);
      }, 10000); // Mostrar após 10 segundos

      return () => clearTimeout(timer);
    }
  }, [isInstallable, isInstalled]);

  // Exibir prompt de notificações após instalação ou primeiro acesso
  useEffect(() => {
    if (notificationPermission === 'default' && isSupported.notifications) {
      const hasShownNotificationPrompt = localStorage.getItem('letz-notification-prompt-shown');

      if (!hasShownNotificationPrompt) {
        const timer = setTimeout(() => {
          setNotificationDialogOpen(true);
          localStorage.setItem('letz-notification-prompt-shown', 'true');
        }, 15000); // Mostrar após 15 segundos

        return () => clearTimeout(timer);
      }
    }
  }, [notificationPermission, isSupported.notifications]);

  // Monitor de conexão
  useEffect(() => {
    if (!isOnline) {
      setOfflineSnackbarOpen(true);
    }
  }, [isOnline]);

  // Ouvir eventos de conteúdo compartilhado
  useEffect(() => {
    const handleSharedContent = (event: any) => {
      const sharedData = event.detail;
      console.log('📤 Conteúdo compartilhado recebido:', sharedData);

      // Redirecionar para criação de evento com dados pré-preenchidos
      if (sharedData.title || sharedData.text) {
        showNotification('LETZ', {
          body: 'Conteúdo compartilhado recebido! Redirecionando...',
          tag: 'shared-content'
        });
      }
    };

    window.addEventListener('sharedcontent', handleSharedContent);
    return () => window.removeEventListener('sharedcontent', handleSharedContent);
  }, [showNotification]);

  const handleInstallApp = async () => {
    const success = await installApp();
    if (success) {
      showNotification('LETZ Instalado!', {
        body: 'O LETZ foi instalado com sucesso em seu dispositivo!',
        tag: 'app-installed'
      });
    }
    setInstallDialogOpen(false);
  };

  const handleRequestNotifications = async () => {
    const granted = await requestNotificationPermission();
    if (granted) {
      showNotification('Notificações Ativadas!', {
        body: 'Você receberá notificações sobre novos eventos e mensagens.',
        tag: 'notifications-enabled'
      });
    }
    setNotificationDialogOpen(false);
  };

  return (
    <>
      {/* FAB para instalação do app */}
      {isInstallable && !isInstalled && (
        <Fab
          color="primary"
          aria-label="instalar app"
          sx={{
            position: 'fixed',
            bottom: 16,
            right: 16,
            zIndex: 1000,
            animation: 'pulse 2s infinite'
          }}
          onClick={() => setInstallDialogOpen(true)}
          disabled={isLoading}
        >
          <Tooltip title="Instalar LETZ">
            <InstallIcon />
          </Tooltip>
        </Fab>
      )}

      {/* Indicador de status de conexão */}
      <Box
        sx={{
          position: 'fixed',
          top: 70,
          right: 16,
          zIndex: 999,
          display: 'flex',
          alignItems: 'center',
          gap: 1,
          background: isOnline ? 'rgba(76, 175, 80, 0.9)' : 'rgba(244, 67, 54, 0.9)',
          color: 'white',
          padding: '4px 8px',
          borderRadius: '16px',
          fontSize: '0.75rem',
          transition: 'all 0.3s ease'
        }}
      >
        {isOnline ? <OnlineIcon fontSize="small" /> : <OfflineIcon fontSize="small" />}
        {isOnline ? 'Online' : 'Offline'}
      </Box>

      {/* Dialog de instalação */}
      <Dialog open={installDialogOpen} onClose={() => setInstallDialogOpen(false)}>
        <DialogTitle>📱 Instalar LETZ</DialogTitle>
        <DialogContent>
          <Typography>
            Instale o LETZ em seu dispositivo para acesso rápido e funcionalidades offline!
          </Typography>
          <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 1 }}>
            <Typography variant="body2" color="text.secondary">
              ✅ Acesso offline aos seus eventos
            </Typography>
            <Typography variant="body2" color="text.secondary">
              ✅ Notificações push
            </Typography>
            <Typography variant="body2" color="text.secondary">
              ✅ Sincronização automática
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setInstallDialogOpen(false)}>Depois</Button>
          <Button 
            onClick={handleInstallApp} 
            variant="contained" 
            disabled={isLoading}
          >
            {isLoading ? 'Instalando...' : 'Instalar'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog de notificações */}
      <Dialog open={notificationDialogOpen} onClose={() => setNotificationDialogOpen(false)}>
        <DialogTitle>🔔 Ativar Notificações</DialogTitle>
        <DialogContent>
          <Typography>
            Ative as notificações para ficar por dentro de novos eventos, mensagens e atualizações!
          </Typography>
          <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 1 }}>
            <Typography variant="body2" color="text.secondary">
              📅 Lembretes de eventos
            </Typography>
            <Typography variant="body2" color="text.secondary">
              💬 Novas mensagens
            </Typography>
            <Typography variant="body2" color="text.secondary">
              👥 Solicitações de amizade
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setNotificationDialogOpen(false)}>Não, obrigado</Button>
          <Button 
            onClick={handleRequestNotifications} 
            variant="contained"
            startIcon={<NotificationsIcon />}
          >
            Ativar Notificações
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar para status offline */}
      <Snackbar
        open={offlineSnackbarOpen && !isOnline}
        onClose={() => setOfflineSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setOfflineSnackbarOpen(false)} 
          severity="warning"
          sx={{ width: '100%' }}
        >
          Você está offline. Algumas funcionalidades podem estar limitadas.
        </Alert>
      </Snackbar>

      {/* CSS para animação de pulse */}
      <style>
        {`
          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }
        `}
      </style>
    </>
  );
}

function AppContent() {
  const { isAuthenticated } = useAppSelector((state) => state.auth);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {isAuthenticated && <Navbar />}

      {/* Gerenciador PWA */}
      <PWAManager />

      <Box component="main" sx={{ flexGrow: 1, pt: isAuthenticated ? 8 : 0 }}>
        <Routes>
          {/* Rotas públicas */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <LoginPage /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <RegisterPage /> : <Navigate to="/dashboard" />} 
          />

          {/* Rotas protegidas */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <DashboardPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events"
            element={
              <ProtectedRoute>
                <EventsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events/create"
            element={
              <ProtectedRoute>
                <CreateEventPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events/:eventId"
            element={
              <ProtectedRoute>
                <EventDetailsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/friends"
            element={
              <ProtectedRoute>
                <FriendsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/profile"
            element={
              <ProtectedRoute>
                <ProfilePage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/notifications"
            element={
              <ProtectedRoute>
                <NotificationsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/messages"
            element={
              <ProtectedRoute>
                <MessagesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/settings"
            element={
              <ProtectedRoute>
                <SettingsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/demo"
            element={
              <ProtectedRoute>
                <DemoPage />
              </ProtectedRoute>
            }
          />

          {/* Novas rotas para funcionalidades avançadas */}
          <Route
            path="/achievements"
            element={
              <ProtectedRoute>
                <div style={{ padding: '20px', textAlign: 'center' }}>
                  <h2>🏆 Conquistas em Desenvolvimento</h2>
                  <p>Sistema de gamificação será implementado em breve!</p>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/analytics"
            element={
              <ProtectedRoute>
                <div style={{ padding: '20px', textAlign: 'center' }}>
                  <h2>📊 Analytics em Desenvolvimento</h2>
                  <p>Dashboard de analytics será implementado em breve!</p>
                </div>
              </ProtectedRoute>
            }
          />

          {/* Rota padrão */}
          <Route 
            path="/" 
            element={
              isAuthenticated ? 
                <Navigate to="/dashboard" /> : 
                <Navigate to="/login" />
            } 
          />

          {/* Rota 404 */}
          <Route 
            path="*" 
            element={
              isAuthenticated ? 
                <Navigate to="/dashboard" /> : 
                <Navigate to="/login" />
            } 
          />
        </Routes>
      </Box>
    </Box>
  );
}

function App() {
  return (
    <Provider store={store}>
      <ThemeProvider>
        <Router
          future={{
            v7_startTransition: true,
            v7_relativeSplatPath: true,
          }}
        >
          <AppContent />
        </Router>
      </ThemeProvider>
    </Provider>
  );
}

export default App;
