import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Box, Snackbar, Alert, Fab, Tooltip, Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store/store';
import { ModernThemeProvider } from './contexts/ModernThemeContext';
import { usePWA } from './hooks/usePWA';
import { 
  InstallMobile as InstallIcon,
  CloudOff as OfflineIcon,
  Wifi as OnlineIcon,
  Notifications as NotificationsIcon
} from '@mui/icons-material';

// P√°ginas Modernas
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/DashboardPage';
import EventsPage from './pages/EventsPage';
import CreateEventPage from './pages/CreateEventPage';
import FriendsPage from './pages/FriendsPage';
import ProfilePage from './pages/ProfilePage';
import EventDetailsPage from './pages/EventDetailsPage';
import NotificationsPage from './pages/NotificationsPage';
import MessagesPage from './pages/MessagesPage';
import SettingsPage from './pages/SettingsPage';
import DemoPage from './pages/DemoPage';

// Componentes Modernos
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

// Hook para verificar autentica√ß√£o
import { useAppSelector } from './hooks/redux';

function PWAManager() {
  const {
    isOnline,
    isInstallable,
    isInstalled,
    isLoading,
    notificationPermission,
    installApp,
    requestNotificationPermission,
    showNotification,
    isSupported
  } = usePWA();

  const [installDialogOpen, setInstallDialogOpen] = React.useState(false);
  const [notificationDialogOpen, setNotificationDialogOpen] = React.useState(false);
  const [offlineSnackbarOpen, setOfflineSnackbarOpen] = React.useState(false);

  // Verificar suporte PWA e exibir prompt de instala√ß√£o ap√≥s delay
  useEffect(() => {
    if (isInstallable && !isInstalled) {
      const timer = setTimeout(() => {
        setInstallDialogOpen(true);
      }, 10000); // Mostrar ap√≥s 10 segundos

      return () => clearTimeout(timer);
    }
  }, [isInstallable, isInstalled]);

  // Exibir prompt de notifica√ß√µes ap√≥s instala√ß√£o ou primeiro acesso
  useEffect(() => {
    if (notificationPermission === 'default' && isSupported.notifications) {
      const hasShownNotificationPrompt = localStorage.getItem('letz-notification-prompt-shown');

      if (!hasShownNotificationPrompt) {
        const timer = setTimeout(() => {
          setNotificationDialogOpen(true);
          localStorage.setItem('letz-notification-prompt-shown', 'true');
        }, 15000); // Mostrar ap√≥s 15 segundos

        return () => clearTimeout(timer);
      }
    }
  }, [notificationPermission, isSupported.notifications]);

  // Monitor de conex√£o
  useEffect(() => {
    if (!isOnline) {
      setOfflineSnackbarOpen(true);
    }
  }, [isOnline]);

  // Ouvir eventos de conte√∫do compartilhado
  useEffect(() => {
    const handleSharedContent = (event: any) => {
      const sharedData = event.detail;
      console.log('üì§ Conte√∫do compartilhado recebido:', sharedData);

      // Redirecionar para cria√ß√£o de evento com dados pr√©-preenchidos
      if (sharedData.title || sharedData.text) {
        showNotification('LETZ', {
          body: 'Conte√∫do compartilhado recebido! Redirecionando...',
          tag: 'shared-content'
        });
      }
    };

    window.addEventListener('sharedcontent', handleSharedContent);
    return () => window.removeEventListener('sharedcontent', handleSharedContent);
  }, [showNotification]);

  const handleInstallApp = async () => {
    const success = await installApp();
    if (success) {
      showNotification('LETZ Instalado!', {
        body: 'O LETZ foi instalado com sucesso em seu dispositivo!',
        tag: 'app-installed'
      });
    }
    setInstallDialogOpen(false);
  };

  const handleRequestNotifications = async () => {
    const granted = await requestNotificationPermission();
    if (granted) {
      showNotification('Notifica√ß√µes Ativadas!', {
        body: 'Voc√™ receber√° notifica√ß√µes sobre novos eventos e mensagens.',
        tag: 'notifications-enabled'
      });
    }
    setNotificationDialogOpen(false);
  };

  return (
    <>
      {/* üé® Indicador de status de conex√£o moderno */}
      <Box
        sx={{
          position: 'fixed',
          top: 80,
          right: 16,
          zIndex: 999,
          display: 'flex',
          alignItems: 'center',
          gap: 1,
          background: isOnline 
            ? 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)' 
            : 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
          color: 'white',
          padding: '8px 16px',
          borderRadius: '20px',
          fontSize: '0.875rem',
          fontWeight: 600,
          backdropFilter: 'blur(20px)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
          transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          transform: isOnline ? 'translateY(0)' : 'translateY(0)',
          opacity: isOnline ? 0.9 : 1,
        }}
      >
        {isOnline ? <OnlineIcon fontSize="small" /> : <OfflineIcon fontSize="small" />}
        {isOnline ? 'Conectado' : 'Modo Offline'}
      </Box>

      {/* üì± Dialog de instala√ß√£o moderno */}
      <Dialog 
        open={installDialogOpen} 
        onClose={() => setInstallDialogOpen(false)}
        PaperProps={{
          sx: {
            borderRadius: '24px',
            background: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(248,250,252,0.9) 100%)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255,255,255,0.2)',
            minWidth: 400,
          }
        }}
      >
        <DialogTitle sx={{ 
          fontSize: '1.5rem', 
          fontWeight: 700,
          background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 50%, #ec4899 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
        }}>
          üöÄ Instalar LETZ
        </DialogTitle>
        <DialogContent sx={{ py: 3 }}>
          <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.6 }}>
            Transforme sua experi√™ncia! Instale o LETZ em seu dispositivo para acesso instant√¢neo e recursos exclusivos.
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #22c55e 0%, #16a34a 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>‚úì</Typography>
              </Box>
              <Typography variant="body2">Acesso offline aos seus eventos favoritos</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>üîî</Typography>
              </Box>
              <Typography variant="body2">Notifica√ß√µes push em tempo real</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>‚ö°</Typography>
              </Box>
              <Typography variant="body2">Sincroniza√ß√£o autom√°tica e r√°pida</Typography>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 0 }}>
          <Button 
            onClick={() => setInstallDialogOpen(false)}
            sx={{ 
              borderRadius: '12px',
              px: 3,
              color: 'text.secondary'
            }}
          >
            Talvez depois
          </Button>
          <Button 
            onClick={handleInstallApp} 
            variant="contained" 
            disabled={isLoading}
            sx={{
              borderRadius: '12px',
              px: 4,
              background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 50%, #ec4899 100%)',
              '&:hover': {
                background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 50%, #ec4899 100%)',
                filter: 'brightness(1.1)',
                transform: 'translateY(-2px)',
              }
            }}
          >
            {isLoading ? 'Instalando...' : 'üöÄ Instalar Agora'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* üîî Dialog de notifica√ß√µes moderno */}
      <Dialog 
        open={notificationDialogOpen} 
        onClose={() => setNotificationDialogOpen(false)}
        PaperProps={{
          sx: {
            borderRadius: '24px',
            background: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(248,250,252,0.9) 100%)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255,255,255,0.2)',
            minWidth: 400,
          }
        }}
      >
        <DialogTitle sx={{ 
          fontSize: '1.5rem', 
          fontWeight: 700,
          background: 'linear-gradient(135deg, #f59e0b 0%, #ec4899 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
        }}>
          üîî Ativar Notifica√ß√µes
        </DialogTitle>
        <DialogContent sx={{ py: 3 }}>
          <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.6 }}>
            N√£o perca nenhum momento especial! Ative as notifica√ß√µes para receber atualiza√ß√µes sobre seus eventos favoritos.
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #ec4899 0%, #be185d 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>üíå</Typography>
              </Box>
              <Typography variant="body2">Novos convites para eventos</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>üë•</Typography>
              </Box>
              <Typography variant="body2">Atualiza√ß√µes de amigos</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ 
                width: 32, 
                height: 32, 
                borderRadius: '8px',
                background: 'linear-gradient(135deg, #06b6d4 0%, #0891b2 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Typography sx={{ color: 'white', fontSize: '1.2rem' }}>‚è∞</Typography>
              </Box>
              <Typography variant="body2">Lembretes de eventos pr√≥ximos</Typography>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 0 }}>
          <Button 
            onClick={() => setNotificationDialogOpen(false)}
            sx={{ 
              borderRadius: '12px',
              px: 3,
              color: 'text.secondary'
            }}
          >
            N√£o agora
          </Button>
          <Button 
            onClick={handleRequestNotifications} 
            variant="contained"
            sx={{
              borderRadius: '12px',
              px: 4,
              background: 'linear-gradient(135deg, #f59e0b 0%, #ec4899 100%)',
              '&:hover': {
                background: 'linear-gradient(135deg, #f59e0b 0%, #ec4899 100%)',
                filter: 'brightness(1.1)',
                transform: 'translateY(-2px)',
              }
            }}
          >
            üîî Ativar Notifica√ß√µes
          </Button>
        </DialogActions>
      </Dialog>

      {/* üì± Snackbar offline moderno */}
      <Snackbar
        open={offlineSnackbarOpen}
        autoHideDuration={6000}
        onClose={() => setOfflineSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setOfflineSnackbarOpen(false)} 
          severity="warning"
          sx={{
            borderRadius: '16px',
            background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
            color: 'white',
            '& .MuiAlert-icon': {
              color: 'white'
            }
          }}
        >
          Voc√™ est√° offline. Algumas funcionalidades podem estar limitadas.
        </Alert>
      </Snackbar>
    </>
  );
}

function AppContent() {
  const { isAuthenticated } = useAppSelector((state) => state.auth);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {isAuthenticated && <Navbar />}

      {/* Gerenciador PWA */}
      <PWAManager />

      <Box component="main" sx={{ flexGrow: 1, pt: isAuthenticated ? 8 : 0 }}>
        <Routes>
          {/* Rotas p√∫blicas */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <LoginPage /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <RegisterPage /> : <Navigate to="/dashboard" />} 
          />

          {/* Rotas protegidas */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <DashboardPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events"
            element={
              <ProtectedRoute>
                <EventsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events/create"
            element={
              <ProtectedRoute>
                <CreateEventPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/events/:eventId"
            element={
              <ProtectedRoute>
                <EventDetailsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/friends"
            element={
              <ProtectedRoute>
                <FriendsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/profile"
            element={
              <ProtectedRoute>
                <ProfilePage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/notifications"
            element={
              <ProtectedRoute>
                <NotificationsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/messages"
            element={
              <ProtectedRoute>
                <MessagesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/settings"
            element={
              <ProtectedRoute>
                <SettingsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/demo"
            element={
              <ProtectedRoute>
                <DemoPage />
              </ProtectedRoute>
            }
          />

          {/* Novas rotas para funcionalidades avan√ßadas */}
          <Route
            path="/achievements"
            element={
              <ProtectedRoute>
                <div style={{ padding: '20px', textAlign: 'center' }}>
                  <h2>üèÜ Conquistas em Desenvolvimento</h2>
                  <p>Sistema de gamifica√ß√£o ser√° implementado em breve!</p>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/analytics"
            element={
              <ProtectedRoute>
                <div style={{ padding: '20px', textAlign: 'center' }}>
                  <h2>üìä Analytics em Desenvolvimento</h2>
                  <p>Dashboard de analytics ser√° implementado em breve!</p>
                </div>
              </ProtectedRoute>
            }
          />

          {/* Rota padr√£o */}
          <Route 
            path="/" 
            element={
              isAuthenticated ? 
                <Navigate to="/dashboard" /> : 
                <Navigate to="/login" />
            } 
          />

          {/* Rota 404 */}
          <Route 
            path="*" 
            element={
              isAuthenticated ? 
                <Navigate to="/dashboard" /> : 
                <Navigate to="/login" />
            } 
          />
        </Routes>
      </Box>
    </Box>
  );
}

function App() {
  return (
    <Provider store={store}>
      <ModernThemeProvider>
        <Router
          future={{
            v7_startTransition: true,
            v7_relativeSplatPath: true,
          }}
        >
          <AppContent />
        </Router>
      </ModernThemeProvider>
    </Provider>
  );
}

export default App;
