import React, { useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Button,
  Chip,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Fab,
  Paper,
} from '@mui/material';
import {
  Add as AddIcon,
  Event as EventIcon,
  People as PeopleIcon,
  TrendingUp as TrendingUpIcon,
  Star as StarIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { fetchUpcomingEvents } from '../store/slices/eventSlice';
import Navbar from '../components/Navbar';

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useAppDispatch();
  const { user } = useAppSelector((state) => state.auth);
  const { upcomingEvents, isLoading } = useAppSelector((state) => state.events);

  useEffect(() => {
    dispatch(fetchUpcomingEvents());
  }, [dispatch]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getEventTypeColor = (type: string) => {
    const colors: { [key: string]: any } = {
      CHURRASCO: 'success',
      FESTA: 'primary',
      JANTAR: 'secondary',
      REUNIAO: 'info',
      ANIVERSARIO: 'warning',
    };
    return colors[type] || 'default';
  };

  const getEventTypeLabel = (type: string) => {
    const labels: { [key: string]: string } = {
      CHURRASCO: 'Churrasco',
      FESTA: 'Festa',
      JANTAR: 'Jantar',
      REUNIAO: 'Reunião',
      ANIVERSARIO: 'Aniversário',
    };
    return labels[type] || type;
  };

  return (
    <>
      <Navbar />
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        {/* Header com boas-vindas */}
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" component="h1" gutterBottom>
            Olá, {user?.firstName}! 👋
          </Typography>
          <Typography variant="subtitle1" color="text.secondary">
            Bem-vindo de volta ao LETZ. O que vamos organizar hoje?
          </Typography>
        </Box>

        <Grid container spacing={3}>
          {/* Cards de Estatísticas */}
          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                    <EventIcon />
                  </Avatar>
                  <Box>
                    <Typography color="textSecondary" gutterBottom>
                      Eventos Criados
                    </Typography>
                    <Typography variant="h5">
                      {user?.eventsCreated || 0}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                    <PeopleIcon />
                  </Avatar>
                  <Box>
                    <Typography color="textSecondary" gutterBottom>
                      Amigos
                    </Typography>
                    <Typography variant="h5">
                      {user?.totalFriends || 0}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: 'warning.main', mr: 2 }}>
                    <StarIcon />
                  </Avatar>
                  <Box>
                    <Typography color="textSecondary" gutterBottom>
                      Pontos
                    </Typography>
                    <Typography variant="h5">
                      {user?.points || 0}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                    <TrendingUpIcon />
                  </Avatar>
                  <Box>
                    <Typography color="textSecondary" gutterBottom>
                      Rank
                    </Typography>
                    <Typography variant="h5">
                      #{user?.rank || '?'}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>

          {/* Próximos Eventos */}
          <Grid item xs={12} md={8}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6" component="h2">
                    Próximos Eventos
                  </Typography>
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => navigate('/events')}
                  >
                    Ver Todos
                  </Button>
                </Box>

                {isLoading ? (
                  <Typography>Carregando...</Typography>
                ) : upcomingEvents.length === 0 ? (
                  <Paper
                    sx={{
                      p: 3,
                      textAlign: 'center',
                      backgroundColor: 'grey.50',
                    }}
                  >
                    <Typography variant="body1" color="text.secondary">
                      Nenhum evento próximo encontrado.
                    </Typography>
                    <Button
                      variant="contained"
                      startIcon={<AddIcon />}
                      sx={{ mt: 2 }}
                      onClick={() => navigate('/events/create')}
                    >
                      Criar Primeiro Evento
                    </Button>
                  </Paper>
                ) : (
                  <List>
                    {upcomingEvents.slice(0, 5).map((event) => (
                      <ListItem
                        key={event.id}
                        sx={{
                          cursor: 'pointer',
                          '&:hover': { backgroundColor: 'action.hover' },
                          borderRadius: 1,
                        }}
                        onClick={() => navigate(`/events/${event.id}`)}
                      >
                        <ListItemAvatar>
                          <Avatar sx={{ bgcolor: 'primary.main' }}>
                            <EventIcon />
                          </Avatar>
                        </ListItemAvatar>
                        <ListItemText
                          primary={
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                              <Typography variant="subtitle2">
                                {event.title}
                              </Typography>
                              <Chip
                                label={getEventTypeLabel(event.type)}
                                size="small"
                                color={getEventTypeColor(event.type)}
                              />
                            </Box>
                          }
                          secondary={
                            <Box>
                              <Typography variant="body2" color="text.secondary">
                                {formatDate(event.startDateTime)}
                              </Typography>
                              <Typography variant="body2" color="text.secondary">
                                📍 {event.location}
                              </Typography>
                            </Box>
                          }
                        />
                      </ListItem>
                    ))}
                  </List>
                )}
              </CardContent>
            </Card>
          </Grid>

          {/* Ações Rápidas */}
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" component="h2" gutterBottom>
                  Ações Rápidas
                </Typography>

                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                  <Button
                    variant="contained"
                    fullWidth
                    startIcon={<AddIcon />}
                    onClick={() => navigate('/events/create')}
                  >
                    Criar Evento
                  </Button>

                  <Button
                    variant="outlined"
                    fullWidth
                    startIcon={<PeopleIcon />}
                    onClick={() => navigate('/friends')}
                  >
                    Gerenciar Amigos
                  </Button>

                  <Button
                    variant="outlined"
                    fullWidth
                    onClick={() => navigate('/events/my')}
                  >
                    Meus Eventos
                  </Button>

                  <Button
                    variant="outlined"
                    fullWidth
                    onClick={() => navigate('/profile')}
                  >
                    Editar Perfil
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* FAB para criar evento */}
        <Fab
          color="primary"
          aria-label="Criar evento"
          sx={{
            position: 'fixed',
            bottom: 16,
            right: 16,
          }}
          onClick={() => navigate('/events/create')}
        >
          <AddIcon />
        </Fab>
      </Container>
    </>
  );
};

export default DashboardPage;
